openai:gpt-5-nano.sampleSchema:
{
  "type": "object",
  "properties": {
    "summary": {
      "type": "string",
      "description": "Brief summary of the analysis",
      "minLength": 1,
      "maxLength": 500
    },
    "items": {
      "type": "array",
      "description": "List of analyzed items",
      "minItems": 0,
      "maxItems": 100,
      "items": {
        "type": "object",
        "properties": {
          "id": { "type": "integer", "description": "Item id" },
          "label": { "type": "string", "description": "Display label" },
          "score": { "type": "number", "minimum": 0, "maximum": 100, "description": "Score 0-100" }
        },
        "required": ["id", "label", "score"],
        "additionalProperties": false
      }
    },
    "totalScore": {
      "type": "number",
      "minimum": 0,
      "maximum": 100,
      "description": "Aggregate score"
    },
    "status": {
      "type": "string",
      "enum": ["pending", "in_progress", "done", "failed"],
      "description": "Current status"
    },
    "optionalLink": {
      "anyOf": [{ "type": "string", "format": "uri" }, { "type": "null" }],
      "description": "Optional reference URL"
    },
    "metadata": {
      "type": "object",
      "properties": {
        "source": { "type": "string", "description": "Data source" },
        "count": { "type": "integer", "minimum": 0, "description": "Item count" }
      },
      "required": ["source", "count"],
      "additionalProperties": false
    },
    "tags": {
      "type": "array",
      "items": { "type": "string" },
      "minItems": 0,
      "description": "Tags"
    }
  },
  "required": ["summary", "items", "totalScore", "status", "metadata", "tags"],
  "additionalProperties": false
}

